import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Auth/Login.tsx';
import Registration from './components/Auth/Registration.tsx';
import QuantumKeyExchange from './components/Auth/QuantumKeyExchange.tsx';
import DarkThemeProvider from './components/Layout/DarkThemeProvider.tsx';
import QuantumEmailClient from './components/Email/QuantumEmailClient.tsx';
import { useAuth, AuthProvider } from './hooks/useAuth.tsx';
import { EncryptionProvider } from './hooks/useEncryption.tsx';
import { NotificationsProvider } from './hooks/useNotifications.tsx';

const AppRoutes: React.FC = () => {
  const { isAuthenticated } = useAuth();

  return (
    <Router>
      <Routes>
        <Route path="/login" element={!isAuthenticated ? <Login /> : <Navigate to="/" />} />
        <Route path="/register" element={!isAuthenticated ? <Registration /> : <Navigate to="/" />} />
        <Route path="/key-exchange" element={isAuthenticated ? <QuantumKeyExchange /> : <Navigate to="/login" />} />
        <Route path="/" element={isAuthenticated ? <QuantumEmailClient /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

const App: React.FC = () => {
  return (
    <DarkThemeProvider>
      <AuthProvider>
        <EncryptionProvider>
          <NotificationsProvider>
            <AppRoutes />
          </NotificationsProvider>
        </EncryptionProvider>
      </AuthProvider>
    </DarkThemeProvider>
  );
};

export default App;
